  callTractor = cart => {
    let auctionDetails = []
    fetch("http://35.161.99.113:9000/webapi/meratractor/bid/myBid", {

      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        user_id: cart
